import React, { useEffect, useState, Component } from "react";
import { Switch, Route, BrowserRouter, Link } from "react-router-dom";
import { useQuery, gql, graphql } from "@apollo/client";
import Login from "./Login";
import LinkList from "./LinkList";
import Items from "./Items";
import Cart from "./Cart";
import "../styles/items.css";

// passing user fetching query
const GET_USER = gql`
  {
    getUser {
      id
      name
      email
      picture
      username
    }
  }
`;

function App() {
  // state for hamburger menu
  const [open, setOpen] = useState(false);
  const [cart, setCart] = useState([]);
  const [products] = useState([
    {
      name: 'Air Jordan 1 Mid "Chicago 2020"',
      cost: "195",
      img:
        "https://www.stadiumgoods.com/cdn-cgi/image/fit%3Dcontain%2Cformat%3Dauto%2Cwidth%3D2000/media/catalog/product/5/5/554724-173_1.png",
    },
    {
      name: 'Air Jordan 6 Retro "Defining Moments"',
      cost: "225",
      img:
        "https://image.goat.com/crop/750/attachments/product_template_additional_pictures/images/032/390/392/original/526250_01.jpg.jpeg?1580925808",
    },
    {
      name: "Jordan 1 Retro High Pine Green Black",
      cost: "170",
      img:
        "https://stockx-360.imgix.net/Air-Jordan-1-Retro-High-Pine-Green-Black/Images/Air-Jordan-1-Retro-High-Pine-Green-Black/Lv2/img20.jpg?auto=format,compress&q=90&updated_at=1606323349&w=1000",
    },
    {
      name: "Air Jordan 4 Retro SE 'What The 4'",
      cost: "200",
      img:
        "https://image.goat.com/crop/750/attachments/product_template_additional_pictures/images/028/286/211/original/503839_01.jpg.jpeg?1573502565",
    },
  ]);

  const addtocart = (product) => {
    console.log("hello");
    setCart([...cart, product]);
  };
  // fetching user
  let user = useQuery(GET_USER).data?.getUser;
  console.log(user);

  return (
    <div>
      <BrowserRouter>
        <nav role="navigation">
          <div id="menuToggle">
            <input type="checkbox" onClick={() => setOpen(!open)} />

            <span></span>
            <span></span>
            <span></span>

            <ul className="hamburgerMenu" id={!open ? "clickedmenu" : ""}>
              <Link
                to="/"
                style={{ textDecoration: "none" }}
                onClick={() => setOpen(!open)}
              >
                <li>Sign In</li>
              </Link>

              <Link
                to="/profile"
                style={{ textDecoration: "none" }}
                onClick={() => setOpen(!open)}
              >
                <li>Profile</li>
              </Link>
            </ul>
          </div>
        </nav>
        {/* <div className="page">
          <h1>Products</h1>
          <button
            onClick={(e) => {
              console.log(cart);
            }}
          >
            Show Cart ({cart.length})
          </button>
          <div className="products">
            {products.map((product, idx) => (
              <div className="itembox" key={idx}>
                <h3>{product.name}</h3>
                <h4>{product.cost}</h4>
                <img className="productimg" src={product.img} />
                <div>
                  <button
                    onClick={(e) => {
                      addtocart(product, idx);
                    }}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div> */}
        <Switch>
          {/* <Route exact path="/" render={(props) => <App {...props} />} /> */}
          <Route exact path="/login" render={(props) => <Login {...props} />} />
          {/* passing the set user */}
          <Route
            exact
            path="/profile"
            render={(props) => <LinkList user={user} {...props} />}
          />
          <Route
            exact
            path="/items"
            render={(props) => <Items user={user} {...props} />}
          />
          <Route
            exact
            path="/cart"
            render={(props) => (
              <Cart user={user} Cart={cart} setCart={setCart} {...props} />
            )}
          />
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
